  .glass-heavy {
    background: var(--glass-heavy);
    backdrop-filter: blur(80px) saturate(180%);
    -webkit-backdrop-filter: blur(80px) saturate(180%);
    border: 1px solid var(--glass-border-strong);
    box-shadow: var(--shadow-float);
  }

  /* Brand Text Effects */
  .brand-text-primary {
    background: var(--brand-primary-gradient);
    background-size: 400% 400%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: brand-gradient-shift 4s ease-in-out infinite;
    filter: drop-shadow(0 0 20px hsla(262, 70%, 54%, 0.5));
  }

  .brand-text-accent {
    background: var(--brand-accent-gradient);
    background-size: 400% 400%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: brand-gradient-shift 6s ease-in-out infinite reverse;
    filter: drop-shadow(0 0 30px hsla(142, 50%, 60%, 0.4));
  }

  .brand-text-hover {
    background: var(--brand-hover-gradient);
    background-size: 400% 400%;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: brand-gradient-shift 5s ease-in-out infinite;
  }

  .brand-glow {
    text-shadow: 
      0 0 5px var(--brand-dark-violet),
      0 0 10px var(--brand-dark-violet),
      0 0 20px var(--brand-dark-violet),
      0 0 40px var(--brand-dark-violet);
  }

  .brand-glow-flame {
    text-shadow: 
      0 0 5px var(--brand-sunburst-flame),
      0 0 10px var(--brand-sunburst-flame),
      0 0 20px var(--brand-sunburst-flame);
  }

  /* Advanced Card Effects */
  .card-premium {
    transition: all var(--duration-medium) var(--easing-expo);
    transform-style: preserve-3d;
  }

  .card-premium:hover {
    transform: 
      perspective(1200px) 
      rotateX(8deg) 
      rotateY(12deg) 
      translateY(-20px) 
      scale(1.02);
    box-shadow: var(--shadow-depth);
  }

  .card-floating {
    animation: float-sophisticated 8s ease-in-out infinite;
    filter: drop-shadow(0 10px 20px rgba(0, 0, 0, 0.3));
  }

  /* Brand Backgrounds */
  .brand-gradient-bg {
    background: 
      radial-gradient(circle at 20% 80%, hsla(262, 70%, 54%, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, hsla(142, 50%, 60%, 0.2) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, hsla(13, 100%, 91%, 0.1) 0%, transparent 50%),
      linear-gradient(135deg, hsl(0, 0%, 0%) 0%, hsl(262, 20%, 5%) 100%);
  }

  .hero-brand {
    background: transparent;
    perspective: 1000px;
    perspective-origin: center center;
  }

  /* Morphing Elements */
  .morph-blob {
    border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
    animation: morph 15s ease-in-out infinite;
  }

  /* Interactive Glow */
  .interactive-glow {
    position: relative;
    transition: all var(--duration-medium) var(--easing-smooth);
  }

  .interactive-glow:hover {
    filter: brightness(1.2);
  }

  .interactive-glow:before {
    content: '';
    position: absolute;
    inset: -2px;
    border-radius: inherit;
    background: var(--holographic-1);
    opacity: 0;
    transition: opacity var(--duration-medium) var(--easing-smooth);
    z-index: -1;
    filter: blur(20px);
  }

  .interactive-glow:hover:before {
    opacity: 0.7;
  }
}

@layer utilities {
  .animate-float-sophisticated {
    animation: float-sophisticated 8s ease-in-out infinite;
  }

  .animate-brand-glow {
    animation: brand-glow-pulse 3s ease-in-out infinite alternate;
  }

  .animate-brand-gradient {
    animation: brand-gradient-shift 4s ease-in-out infinite;
  }

  .animate-marquee-smooth {
    animation: marquee-smooth 40s linear infinite;
  }

  .animate-counter-cinematic {
    animation: counter-cinematic 2.5s var(--easing-expo);
  }

  .animate-particle-galaxy {
    animation: particle-galaxy 12s linear infinite;
  }

  .animate-spin-cinematic {
    animation: spin-cinematic 20s linear infinite;
  }

  .animate-morph {
    animation: morph 15s ease-in-out infinite;
  }

  .animate-pulse-neon {
    animation: pulse-neon 2s ease-in-out infinite alternate;
  }

  .animate-reveal-cinematic {
    animation: reveal-cinematic 1.5s var(--easing-expo) forwards;
  }
}

@keyframes float-sophisticated {
  0%, 100% { 
    transform: translateY(0px) rotateZ(0deg) scale(1); 
  }
  25% { 
    transform: translateY(-15px) rotateZ(1deg) scale(1.02); 
  }
  50% { 
    transform: translateY(-30px) rotateZ(0deg) scale(1); 
  }
  75% { 
    transform: translateY(-15px) rotateZ(-1deg) scale(0.98); 
  }
}

@keyframes brand-glow-pulse {
  0% { 
    box-shadow: var(--shadow-brand-violet);
    filter: brightness(1) saturate(100%);
  }
  100% { 
    box-shadow: var(--shadow-brand-flame);
    filter: brightness(1.2) saturate(130%);
  }
}

@keyframes brand-gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  25% { background-position: 100% 50%; }
  50% { background-position: 100% 100%; }
  75% { background-position: 0% 100%; }
}

@keyframes marquee-smooth {
  0% { transform: translateX(100%); }
  100% { transform: translateX(-100%); }
}

@keyframes counter-cinematic {
  0% { 
    opacity: 0; 
    transform: translateY(40px) scale(0.8) rotateX(90deg); 
  }
  50% { 
    opacity: 0.8; 
    transform: translateY(-10px) scale(1.1) rotateX(0deg); 
  }
  100% { 
    opacity: 1; 
    transform: translateY(0px) scale(1) rotateX(0deg); 
  }
}

@keyframes particle-galaxy {
  0% { 
    transform: translateY(100vh) translateX(0px) rotateZ(0deg) scale(0); 
    opacity: 0; 
  }
  5% { 
    opacity: 1; 
    transform: translateY(95vh) translateX(10px) rotateZ(45deg) scale(1); 
  }
  95% { 
    opacity: 1; 
    transform: translateY(-5vh) translateX(90px) rotateZ(315deg) scale(1); 
  }
  100% { 
    transform: translateY(-10vh) translateX(100px) rotateZ(360deg) scale(0); 
    opacity: 0; 
  }
}

@keyframes spin-cinematic {
  0% { transform: rotateY(0deg) rotateX(0deg); }
  25% { transform: rotateY(90deg) rotateX(5deg); }
  50% { transform: rotateY(180deg) rotateX(0deg); }
  75% { transform: rotateY(270deg) rotateX(-5deg); }
  100% { transform: rotateY(360deg) rotateX(0deg); }
}

@keyframes morph {
  0%, 100% { 
    border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
    transform: scale(1) rotateZ(0deg);
  }
  25% { 
    border-radius: 30% 60% 70% 40% / 50% 60% 30% 60%;
    transform: scale(1.05) rotateZ(90deg);
  }
  50% { 
    border-radius: 50% 60% 30% 60% / 60% 30% 60% 40%;
    transform: scale(0.95) rotateZ(180deg);
  }
  75% { 
    border-radius: 60% 40% 60% 30% / 40% 70% 40% 50%;
    transform: scale(1.02) rotateZ(270deg);
  }
}

@keyframes pulse-neon {
  0% { 
    opacity: 0.8;
    filter: hue-rotate(0deg) brightness(1);
  }
  100% { 
    opacity: 1;
    filter: hue-rotate(90deg) brightness(1.4);
  }
}

@keyframes reveal-cinematic {
  0% { 
    opacity: 0;
    transform: translateY(100px) rotateX(90deg) scale(0.8);
    filter: blur(20px);
  }
  60% { 
    opacity: 0.8;
    transform: translateY(-20px) rotateX(0deg) scale(1.05);
    filter: blur(5px);
  }
  100% { 
    opacity: 1;
    transform: translateY(0px) rotateX(0deg) scale(1);
    filter: blur(0px);
  }
}

/* Reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  .card-hover:hover, .tilt-effect:hover {
    transform: none;
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(0 0% 0%);
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, var(--neon-purple), var(--neon-blue));
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(135deg, var(--neon-blue), var(--neon-green));
}

