import { motion, useScroll, useTransform } from "framer-motion";
import { Button } from "@/components/ui/button";
import { ArrowRight, Calendar, Sparkles } from "lucide-react";
import ThreeBackground from "./three-background";
import { useReducedMotion } from "@/hooks/use-reduced-motion";
import { useEffect, useRef, useState } from "react";

export default function HeroSection() {
  const prefersReducedMotion = useReducedMotion();
  const sectionRef = useRef<HTMLElement>(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Cinematic intro animation states
  const [introPhase, setIntroPhase] = useState<'particles' | 'buildup' | 'climax' | 'transition' | 'content'>('particles');
  const [particleOpacity, setParticleOpacity] = useState(1.0); // Start at 100% opacity
  const [contentVisible, setContentVisible] = useState(false);
  const [cinematicScale, setCinematicScale] = useState(1.2); // Start zoomed in
  const [cinematicBlur, setCinematicBlur] = useState(2); // Start with subtle blur

  const { scrollYProgress } = useScroll({
    target: sectionRef,
    offset: ["start start", "end start"]
  });

  const y = useTransform(scrollYProgress, [0, 1], [0, -100]);
  const opacity = useTransform(scrollYProgress, [0, 0.8], [1, 0]);

  // Modern scroll-triggered flip animation for particles
  const particleRotateX = useTransform(scrollYProgress, [0, 0.3, 0.7, 1], [0, 15, 45, 180]);
  const particleScale = useTransform(scrollYProgress, [0, 0.3, 0.7, 1], [1, 1.05, 0.95, 0.8]);
  const particleOpacityScroll = useTransform(scrollYProgress, [0, 0.4, 0.8, 1], [1, 0.8, 0.5, 0.2]);

  // Intro animation sequence
  useEffect(() => {
    if (prefersReducedMotion) {
      // Skip intro for reduced motion
      setIntroPhase('content');
      setParticleOpacity(0.8);
      setContentVisible(true);
      setCinematicScale(1.0);
      setCinematicBlur(0);
      return;
    }

    const cinematicSequence = [
      // Phase 1: Start with full particles at 100% opacity (2 seconds)
      { delay: 0, phase: 'particles' as const, opacity: 1.0, content: false, scale: 1.2, blur: 2 },

      // Phase 2: Dramatic buildup - particles intensify (1.5 seconds)
      { delay: 2000, phase: 'buildup' as const, opacity: 1.2, content: false, scale: 1.1, blur: 1 },
      { delay: 2800, phase: 'buildup' as const, opacity: 1.5, content: false, scale: 1.05, blur: 0.5 },

      // Phase 3: Cinematic climax - maximum intensity (1 second)
      { delay: 3500, phase: 'climax' as const, opacity: 1.8, content: false, scale: 1.0, blur: 0 },

      // Phase 4: Smooth transition with content reveal (1.5 seconds)
      { delay: 4500, phase: 'transition' as const, opacity: 1.2, content: true, scale: 1.0, blur: 0 },
      { delay: 5500, phase: 'transition' as const, opacity: 0.8, content: true, scale: 1.0, blur: 0 },

      // Phase 5: Final state - refined and elegant
      { delay: 6000, phase: 'content' as const, opacity: 0.6, content: true, scale: 1.0, blur: 0 }
    ];

    cinematicSequence.forEach(({ delay, phase, opacity, content, scale, blur }) => {
      setTimeout(() => {
        setIntroPhase(phase);
        setParticleOpacity(opacity);
        setContentVisible(content);
        setCinematicScale(scale);
        setCinematicBlur(blur);
      }, delay);
    });
  }, [prefersReducedMotion]);

  // Mouse parallax effect
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      if (prefersReducedMotion) return;
      const { clientX, clientY } = e;
      const x = (clientX / window.innerWidth - 0.5) * 20;
      const y = (clientY / window.innerHeight - 0.5) * 20;
      setMousePosition({ x, y });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [prefersReducedMotion]);

  const cinematicVariants = {
    hidden: { 
      opacity: 0, 
      y: 30
    },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: prefersReducedMotion ? 0.01 : 0.8,
        ease: [0.19, 1, 0.22, 1]
      }
    }
  };

  const staggerContainer = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: prefersReducedMotion ? 0 : 0.15,
        delayChildren: 0.3
      }
    }
  };

  const wordVariants = {
    hidden: { 
      opacity: 0, 
      y: 100,
      rotateX: 90
    },
    visible: (custom: number) => ({
      opacity: 1, 
      y: 0,
      rotateX: 0,
      transition: { 
        duration: prefersReducedMotion ? 0.01 : 0.8,
        ease: [0.19, 1, 0.22, 1],
        delay: custom * 0.2
      }
    })
  };

  return (
    <section 
      ref={sectionRef}
      className="relative min-h-screen flex items-center justify-center overflow-hidden hero-brand"
      data-testid="hero-section"
    >
      <motion.div
        className="absolute inset-0"
        style={{
          rotateX: prefersReducedMotion ? 0 : particleRotateX,
          scale: prefersReducedMotion ? 1 : particleScale,
          opacity: prefersReducedMotion ? 1 : particleOpacityScroll,
          transformStyle: 'preserve-3d',
          transformOrigin: 'center center'
        }}
      >
        <ThreeBackground 
          introOpacity={particleOpacity} 
          cinematicPhase={introPhase}
        />
      </motion.div>


      <motion.div 
        className={`relative z-20 text-center max-w-7xl mx-auto px-6 py-8 pt-20 transition-all duration-1500 ease-out ${
          contentVisible ? 'opacity-100' : 'opacity-0 pointer-events-none'
        }`}
        style={{ 
          y: prefersReducedMotion ? 0 : y, 
          opacity: prefersReducedMotion ? 1 : (contentVisible ? opacity : 0),
          transform: prefersReducedMotion ? 'none' : `translate(${mousePosition.x * 0.5}px, ${mousePosition.y * 0.5}px) scale(${cinematicScale})`,
          filter: prefersReducedMotion ? 'none' : `blur(${cinematicBlur}px)`,
          transition: 'all 1.5s cubic-bezier(0.19, 1, 0.22, 1)'
        }}
        variants={staggerContainer}
        initial="hidden"
        animate={contentVisible ? "visible" : "hidden"}
      >

        {/* Cinematic Headline */}
        <motion.div className="mb-6" variants={cinematicVariants}>
          <h1 
            className="text-5xl md:text-7xl xl:text-8xl font-black leading-tight tracking-tight" 
            data-testid="hero-headline"
            style={{
              textShadow: '0 0 40px rgba(255,255,255,0.3)'
            }}
          >
            <span className="block text-white font-black">Get your</span>
            <span className="block relative my-2">
              <span className="bg-white text-black font-black px-16 py-0 inline-block transform rotate-[-1deg] text-8xl md:text-10xl xl:text-11xl">brand</span>
            </span>
            <span className="block text-white font-black">Noticed</span>
          </h1>
        </motion.div>

        <motion.div
          variants={cinematicVariants}
          className="mb-8"
        >
          <p 
            className="text-lg md:text-xl xl:text-2xl text-white/80 max-w-3xl mx-auto leading-relaxed font-light px-4"
            data-testid="hero-description"
            style={{ 
              fontFamily: 'var(--font-sans)',
              textShadow: '0 0 20px rgba(255,255,255,0.1)'
            }}
          >
            Revolutionary <span className="text-white/90 font-semibold">AI-powered</span> brand activations that create 
            <span className="text-white/90 font-semibold"> unforgettable experiences</span> and drive 
            <span className="text-white/90 font-semibold">massive engagement</span>
          </p>
        </motion.div>

        <motion.div 
          className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-12 px-4"
          variants={cinematicVariants}
        >
          <Button 
            size="lg"
            className="group px-6 py-3 text-base font-bold hover:scale-105 transition-all duration-500 backdrop-blur-2xl border-2 border-white/20 rounded-xl bg-white/10 hover:bg-white/20 w-full sm:w-auto"
            data-testid="cta-custom-concept"
          >
            <span className="text-white font-bold">
              Request a Custom Concept
            </span>
            <ArrowRight className="ml-2 group-hover:translate-x-1 transition-transform duration-300" size={18} />
          </Button>

          <Button 
            variant="outline"
            size="lg"
            className="group px-6 py-3 text-base font-semibold hover:scale-105 transition-all duration-500 border-2 border-white/30 rounded-xl backdrop-blur-xl bg-transparent hover:bg-white/10 w-full sm:w-auto"
            data-testid="cta-consultation"
          >
            <span className="text-white group-hover:text-white/80 transition-colors font-semibold">
              Schedule a Virtual Consultation
            </span>
            <Calendar className="ml-2 group-hover:rotate-6 transition-transform duration-300" size={18} />
          </Button>
        </motion.div>

        {/* Scroll indicator */}
        <motion.div
          variants={cinematicVariants}
          className="mt-8"
        >
          <div className="flex flex-col items-center gap-3">
            <span className="text-white/60 text-xs font-light tracking-widest">SCROLL TO EXPLORE</span>
            <div className="w-6 h-10 border-2 border-white/30 rounded-full flex justify-center">
              <div className="w-1 h-3 bg-white/60 rounded-full mt-2 animate-bounce" />
            </div>
          </div>
        </motion.div>
      </motion.div>
    </section>
  );
}

